mapper.insert:
INSERT INTO BOOK (  BOOK_NO
					, BOOK_ORIGIN_NAME
					, BOOK_CHANGE_NAME
					, BOOK_TITLE
					, BOOK_AUTHOR
					, BOOK_CATEGORY
					, BOOK_PRICE
					, PUBLISHER
					, BOOK_DATE
					, BOOK_INFO
					) VALUES (
						SEQ_BKNO.NEXTVAL
						, #{bookOriginName}
						, #{bookChangeName}
						, #{bookTitle}
						, #{bookAuthor}
						, #{bookCategory}
						, #{bookPrice}
						, #{publisher}
						, #{bookDate}
						, #{bookInfo}
					)

