mapper.update:
    UPDATE PRODUCT
	SET PRODUCT_TITLE = #{productTitle}
           ,PRODUCT_PRICE = #{productPrice}
           ,PRODUCT_CONTENT = #{productContent}
	WHERE PRODUCT_NO = #{productNo}
	AND SALES_STATUS = 'Y'
    AND FILE_TYPE = 2

 	UPDATE ATTACHMENT
	SET ORIGIN_NAME = #{originName}
	   ,CHANGE_NAME = #{changeName}
	   ,FILE_PATH = #{filePath}
	WHERE REF_NO = #{refNo}
	AND STATUS = 'Y'
	AND FILE_TYPE = 2

UPDATE PRODUCT
SET LIKE_COUNT = LIKE_COUNT+1
WHERE PRODUCT_NO = #{productNo}

mapper.select:
SELECT LIKE_COUNT
FROM PRODUCT
WHERE PRODUCT_NO = #{productNo}

mapper.delete:
DELETE FROM PRODUCT_LIKE
WHERE PRODUCT_NO = #{productNo}
AND USER_NO = #{userNo}

mapper.update:
UPDATE PRODUCT
SET LIKE_COUNT = LIKE_COUNT-1
WHERE PRODUCT_NO = #{productNo}

mapper.select:
	SELECT COUNT(*)
  FROM PRODUCT_LIKE
  WHERE PRODUCT_NO = #{productNo}
  AND USER_NO = #{userNo}
